div.script:
var viewSpecBtn = document.querySelector('.ian-popup__view-spec-btn');
var viewSpecArrowUp = document.querySelector('.ian-popup__viewspec--up');
var viewSpecArrowDown = document.querySelector('.ian-popup__viewspec--down');
var viewSpec = document.querySelector('.ian-popup__product-specification');

viewSpecBtn.addEventListener("click", function(e){
e.preventDefault();
viewSpecArrowUp.classList.toggle('d-none');
viewSpecArrowDown.classList.toggle('d-none');
viewSpec.classList.toggle('d-none');
});

xpath.script:
// Search input trigger
let searchOpen = document.querySelector('#openSearch');
let searchClose = document.querySelector('#closeSearch');
let searchDialog = document.querySelector('.ian-header__search');


// Open Search Dialog
searchOpen.addEventListener('click', function (e) {
e.preventDefault();
searchDialog.classList.toggle('d-none');

});
// Close Search Dialog
searchClose.addEventListener('click', function (e) {
e.preventDefault();
searchDialog.classList.toggle('d-none');
});

// Open / Close Customer Support Modal
let supportToggle = document.querySelector('#openSupport');
let supportModal = document.querySelector('.ian-header__support-detail');

supportToggle.addEventListener('click', function (e) {
supportModal.classList.toggle('d-none');
});

// Mobile Navigation Toggle
let mobileToggler = document.querySelector('#menuToggle');
let mobileMenu = document.querySelector('.ian-header__nav');

mobileToggler.addEventListener('click', function (e) {
e.preventDefault();
mobileMenu.classList.toggle('ian-header__nav-hide');
mobileToggler.classList.toggle('is-active')
});
