function filterDiv() {
                            document.getElementById("filterDropDown").classList.toggle("show-div");
                            } function myFunction() {
                            document.getElementById("myDropdown").classList.toggle("show-div");
                            } function filterFunction() { var input, filter, ul,
                            li, a, i; input =
                            document.getElementById("myInput"); filter =
                            input.value.toUpperCase(); div =
                            document.getElementById("myDropdown"); a =
                            div.getElementsByTagName("a"); for (i = 0; i
                            < a.length
                            ; i++) { txtValue = a[i].textContent ||
                            a[i].innerText; if
                            (txtValue.toUpperCase().indexOf(filter) > -1) {
                            a[i].style.display = ""; } else { a[i].style.display
                            = "none"; } } }

t.script:
function open_tag_modal()
{
$('#addTagModal').modal('show');
}
function closeModal()
{
document.getElementById('addTagModal').style.display = "none";
}

